const PartnersComponent = () => {
    const [partners, setPartners] = useState([]);

    useEffect(() => {
      // Remplacez l'URL ci-dessous par l'URL de votre API WordPress
      const apiUrl = 'http://cjezdhm.cluster029.hosting.ovh.net/wp-json/wp/v2/partners?per_page=20&categories';

      axios.get(apiUrl)
        .then(response => {
          setPartners(response.data);
        })
        .catch(error => {
          console.error('Error fetching partners:', error);
        });
    }, []);

    const extractImageFromContent = content => {
      const match = content.match(/<img[^>]+src="([^">]+)"/);
      return match ? match[1] : null;
    };

    const filterPartnersByCategory = category => {
      return partners.filter(partner => partner.categories.includes(category));
    };

    const mediaPartners = filterPartnersByCategory('partenaires-medias');
    const institutionnalPartners = filterPartnersByCategory('partenaires-institutionnels');

    return (
      <div className='partenaires text-center pt-5 pb-5 px-3'>
        <h2>PARTENAIRES</h2>
        <div>
          <h3>Partenaires MÃ©dias</h3>
          {mediaPartners.map(partner => (
            <div key={partner.id}>
              <h4>{partner.title.rendered}</h4>
              {partner.content && (
                <img src={extractImageFromContent(partner.content.rendered)} alt={partner.title.rendered} />
              )}
            </div>
          ))}
        </div>
        <div>
          <h3>Partenaires Institutionnels</h3>
          {institutionnalPartners.map(partner => (
            <div key={partner.id}>
              <h4>{partner.title.rendered}</h4>
              {partner.content && (
                <img src={extractImageFromContent(partner.content.rendered)} alt={partner.title.rendered} />
              )}
            </div>
          ))}
        </div>
      </div>
    );
  };

  export default PartnersComponent;
