import React, { useEffect, useState } from 'react';

const Programmation = () => {
  const [concerts, setConcerts] = useState([]);

  useEffect(() => {
    // Utilisez une requête AJAX ou un moyen d'obtenir les données des événements depuis WordPress
    // Remplacez l'URL par celle de votre site WordPress avec Events Calendar
    fetch('https://concrete-jungle-festival.com/wp-json/wp/v2/events')
      .then(response => response.json())
      .then(data => setConcerts(data))
      .catch(error => console.error('Erreur lors de la récupération des événements :', error));
  }, []);

  return (
    <div>
      <h2>Programmation des Concerts</h2>
      <ul>
        {concerts.map(concert => (
          <li key={concert.id}>
            <h3>{concert.title.rendered}</h3>
            <p>Date: {concert.start_date}</p>
            <p>Heure: {concert.start_time}</p>
            <p>Lieu: {concert.venue}</p>
            {/* Ajoutez d'autres détails spécifiques à votre programmation */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Programmation;
