import React, { useState } from 'react';

const HomePage = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  return (
    <div>
      {/* ... Autres éléments de la page d'accueil ... */}

      {/* Contrôle de filtre */}
      <select value={selectedCategory} onChange={handleCategoryChange}>
        <option value="all">Toutes les catégories</option>
        <option value="scenes">Scènes</option>
        <option value="wc">WC</option>
        <option value="restaurant">Restaurant</option>
      </select>

      {/* Intégration de la carte avec le shortcode dans une iframe */}
      <iframe
        title="Carte du Festival"
        src={`http://cjezdhm.cluster029.hosting.ovh.net/map/&category=${selectedCategory}`}
        width="100%"
        height="400"
        frameBorder="0"
        style={{ border: 0 }}
        allowFullScreen=""
        aria-hidden="false"
        tabIndex="0"
      ></iframe>
    </div>
  );
};

export default HomePage;
