import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Partners.css';

const PartnersComponent = () => {
  const [partners, setPartners] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(''); // Ajoutez un state pour stocker la catégorie sélectionnée

  useEffect(() => {
    const apiUrl = `http://cjezdhm.cluster029.hosting.ovh.net/wp-json/wp/v2/partners?per_page=20${selectedCategory ? `&filter[category_name]=${selectedCategory}` : ''}`;

    axios.get(apiUrl)
      .then(response => {
        setPartners(response.data);
      })
      .catch(error => {
        console.error('Error fetching partners:', error);
      });
  }, [selectedCategory]);

  const extractImageFromContent = content => {
    const match = content.match(/<img[^>]+src="([^">]+)"/);
    return match ? match[1] : null;
  };

  return (
    <div className='partenaires text-center pt-5 pb-5 px-3'>
      <h2>PARTENAIRES</h2>

      {/* Ajoutez des boutons ou un sélecteur pour choisir la catégorie */}
      <div className="categories-selector">
        <button onClick={() => setSelectedCategory('')} className={selectedCategory === '' ? 'active' : ''}>Tous</button>
        <button onClick={() => setSelectedCategory('medias')} className={selectedCategory === 'medias' ? 'active' : ''}>Médias</button>
        <button onClick={() => setSelectedCategory('institutionnels')} className={selectedCategory === 'institutionnels' ? 'active' : ''}>Institutionnels</button>
      </div>

      <div className="d-flex flex-wrap justify-content-around align-items-center">
        {partners.map(partner => (
          <div key={partner.id} className="col-12 col-md-4 col-lg-3 mt-3">
            {partner.content && (
              <>
                <img src={extractImageFromContent(partner.content.rendered)} alt={partner.title.rendered} className='img-fluid' />
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PartnersComponent;
