import React, { useState, useEffect } from 'react';
import logo from '../images/logo.png';
import '../styles/Header.css';
import { NavLink } from 'react-router-dom';

function Header() {
  const [isFixed, setIsFixed] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const triggerPosition = 100;

      if (scrollPosition > triggerPosition) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className={`header ${isFixed ? 'fixed-top' : ''}`}>
      <header className="navbar navbar-expand-sm px-3 mb-2">
        <NavLink to="/" className="logo-container">
          <img src={logo} alt="logo" className="logo" />
        </NavLink>
        <button
          className="navbar-toggler"
          type="button"
          onClick={toggleMenu}
          aria-controls="navbar-content"
          aria-expanded={isMenuOpen}
        >
          <span className="navbar-toggler-icon" />
        </button>
        <nav
          className={`navbar-nav ms-auto d-flex align-items-sm-center ${
            isMenuOpen ? 'show' : ''
          }`}
        >
          <NavLink to="/" className="nav-link text-white me-3" onClick={toggleMenu}>
            Acceuil
          </NavLink>
          <NavLink
            to="/Programmation"
            className="nav-link text-white me-3"
            onClick={toggleMenu}
          >
            Programmation
          </NavLink>
        </nav>
      </header>
    </div>
  );
}

export default Header;
