import React, { useEffect, useState } from 'react';
import '../styles/Programmation.css';

const Programmation = () => {
  const [concerts, setConcerts] = useState([]);
  const [filteredConcerts, setFilteredConcerts] = useState([]);
  const [filterDate, setFilterDate] = useState(''); // État pour le filtre par date
  const [filterTime, setFilterTime] = useState(''); // État pour le filtre par heure
  const [filterVenue, setFilterVenue] = useState(''); // État pour le filtre par lieu

  useEffect(() => {
    // Utilisez une requête AJAX pour récupérer les événements depuis WordPress
    fetch('http://localhost:8888/concrete-jungle-festival/wp-json/tribe/events/v1/events')
      .then(response => response.json())
      .then(data => {
        setConcerts(data.events);
        setFilteredConcerts(data.events); // Initialise les concerts filtrés avec tous les concerts
      })
      .catch(error => console.error('Erreur lors de la récupération des événements :', error));
  }, []);

  // Fonction de filtrage des concerts en fonction des filtres sélectionnés
  const filterConcerts = () => {
    let filtered = [...concerts];

    // Filtrer par date
    if (filterDate !== '') {
      filtered = filtered.filter(concert => concert.start_date.includes(filterDate));
    }

    // Filtrer par heure
    if (filterTime !== '') {
      filtered = filtered.filter(concert => concert.start_date.includes(filterTime));
    }

    // Filtrer par lieu
    if (filterVenue !== '') {
      filtered = filtered.filter(concert => concert.venue.venue.includes(filterVenue));
    }

    setFilteredConcerts(filtered);
  };

  // Appeler la fonction de filtrage lorsque les filtres changent
  useEffect(() => {
    filterConcerts();
  }, [filterDate, filterTime, filterVenue]);

  return (
    <div className='programmation'>
      <h2 className='pt-3 pb-4 text-center display-5'>PROGRAMMATION</h2>

      {/* Filtre par date */}
      <label htmlFor="filterDate">Filtrer par Date:</label>
      <select id="filterDate" value={filterDate} onChange={(e) => setFilterDate(e.target.value)}>
        <option value="">Toutes les dates</option>
        {/* Ajoutez ici les options de date dynamiquement en fonction de vos données */}
      </select>

      {/* Filtre par heure */}
      <label htmlFor="filterTime">Filtrer par Heure:</label>
      <select id="filterTime" value={filterTime} onChange={(e) => setFilterTime(e.target.value)}>
        <option value="">Toutes les heures</option>
        {/* Ajoutez ici les options d'heure dynamiquement en fonction de vos données */}
      </select>

      {/* Filtre par lieu */}
      <label htmlFor="filterVenue">Filtrer par Lieu:</label>
      <select id="filterVenue" value={filterVenue} onChange={(e) => setFilterVenue(e.target.value)}>
        <option value="">Tous les lieux</option>
        {/* Ajoutez ici les options de lieu dynamiquement en fonction de vos données */}
      </select>

      <ul>
        {filteredConcerts.map(concert => (
          <React.Fragment key={concert.id}>
            <li>
              <h3>{concert.title}</h3>
              <p>Date et Heure: {concert.start_date}</p>
              <p>{concert.venue.venue}</p>
            </li>
            <hr className='horizontal-line' />
          </React.Fragment>
        ))}
      </ul>
    </div>
  );
};

export default Programmation;
