import React, { useEffect, useState } from 'react';

const Partners = () => {
  const [partners, setPartners] = useState([]);

  useEffect(() => {
    // Utilisez une requête AJAX pour récupérer les partenaires depuis WordPress
    fetch('http://votre-site.com/wp-json/wp/v2/partners?_embed')
      .then(response => response.json())
      .then(data => setPartners(data))
      .catch(error => console.error('Erreur lors de la récupération des partenaires :', error));
  }, []);

  return (
    <div>
      <h2>Partenaires</h2>
      <ul>
        {partners.map(partner => (
          <li key={partner.id}>
            <h3>{partner.title.rendered}</h3>
            {/* Accéder à l'image du logo */}
            {partner._embedded && partner._embedded['wp:featuredmedia'] && (
              <img
                src={partner._embedded['wp:featuredmedia'][0].source_url}
                alt={partner.title.rendered}
              />
            )}
            {/* Accéder à d'autres champs ACF si nécessaire */}
            {partner.acf && partner.acf.autreChamp && <p>{partner.acf.autreChamp}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Partners;
