import React, { useEffect, useState } from 'react';
import '../styles/Programmation.css';

const Programmation = () => {
  const [concerts, setConcerts] = useState([]);
  const [tri, setTri] = useState('date'); // 'date', 'heure', ou 'lieu'

  useEffect(() => {
    // Utilisez une requête AJAX pour récupérer les événements depuis WordPress
    fetch('http://localhost:8888/concrete-jungle-festival/wp-json/tribe/events/v1/events')
      .then(response => response.json())
      .then(data => setConcerts(data.events))
      .catch(error => console.error('Erreur lors de la récupération des événements :', error));
  }, []);

  // Fonction de tri
  const trierConcerts = (type) => {
    const sortedConcerts = [...concerts];

    switch (type) {
      case 'date':
        sortedConcerts.sort((a, b) => new Date(a.start_date) - new Date(b.start_date));
        break;
      case 'heure':
        sortedConcerts.sort((a, b) => new Date(a.start_date).getTime() - new Date(b.start_date).getTime());
        break;
      case 'lieu':
        sortedConcerts.sort((a, b) => a.venue.venue.localeCompare(b.venue.venue));
        break;
      default:
        break;
    }

    setConcerts(sortedConcerts);
    setTri(type);
  };

  return (
    <div className='programmation'>
      <h2 className='pt-3 pb-4 text-center display-5'>PROGRAMMATION</h2>

      {/* Boutons de tri */}
      <div className="tri-buttons">
        <button onClick={() => trierConcerts('date')}>Trier par Date</button>
        <button onClick={() => trierConcerts('heure')}>Trier par Heure</button>
        <button onClick={() => trierConcerts('lieu')}>Trier par Lieu</button>
      </div>

      <ul>
        {concerts.map(concert => (
          <React.Fragment key={concert.id}>
            <li>
              <h3>{concert.title}</h3>
              <p>Date et Heure: {concert.start_date}</p>
              <p>{concert.venue.venue}</p>
            </li>
            <hr />
          </React.Fragment>
        ))}
      </ul>
    </div>
  );
};

export default Programmation;
