import React, { useState, useEffect } from 'react';
import Iframe from 'react-iframe';

const Map = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [iframeUrl, setIframeUrl] = useState('http://cjezdhm.cluster029.hosting.ovh.net/map/');

  useEffect(() => {
    const urlWithParams = `${iframeUrl}?category=${selectedCategory}`;
    setIframeUrl(urlWithParams);
  }, [selectedCategory, iframeUrl]);

  const handleCategoryChange = (event) => {
    const category = event.target.value;
    setSelectedCategory(category);
  };

  return (
    <div>
      <label htmlFor="categoryFilter">Filtrer par catégorie :</label>
      <select id="categoryFilter" value={selectedCategory} onChange={handleCategoryChange}>
        <option value="">Toutes les catégories</option>
        <option value="scenes">Scènes</option>
        <option value="wc">WC</option>
        <option value="restaurant">Restaurant</option>
      </select>

      <Iframe
        url={iframeUrl}
        width="100%"
        height="400"
        frameBorder="0"
        allowFullScreen=""
      />
    </div>
  );
};

export default Map;
