// Partners.js

import React, { useState, useEffect } from 'react';

const Partners = () => {
  const [partners, setPartners] = useState([]);

  useEffect(() => {
    // Requête AJAX pour récupérer les partenaires depuis WordPress
    fetch('http://votre-site.com/wp-json/wp/v2/partners?_embed')
      .then(response => response.json())
      .then(data => setPartners(data))
      .catch(error => console.error('Erreur lors de la récupération des partenaires :', error));
  }, []);

  return (
    <div>
      <h2>Partenaires</h2>
      <ul>
        {partners.map(partner => (
          <li key={partner.id}>
            <h3>{partner.title.rendered}</h3>
            <div dangerouslySetInnerHTML={{ __html: partner.content.rendered }} />

            {/* Récupération du logo à partir du champ ACF */}
            {partner._embedded && partner._embedded['wp:featuredmedia'] && (
              <img
                src={partner._embedded['wp:featuredmedia'][0].source_url}
                alt={partner._embedded['wp:featuredmedia'][0].alt_text}
                style={{ maxWidth: '100px' }}
              />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Partners;
