import React, { useEffect, useState } from 'react';

const Programmation = () => {
  const [concerts, setConcerts] = useState([]);

  useEffect(() => {
    // Utilisez une requête AJAX pour récupérer les événements depuis WordPress
    fetch('http://localhost:8888/concrete-jungle-festival/wp-json/tribe/events/v1/events')
      .then(response => response.json())
      .then(data => setConcerts(data.events))
      .catch(error => console.error('Erreur lors de la récupération des événements :', error));
  }, []);

  return (
    <div>
      <h2>Programmation des Concerts</h2>
      <ul>
        {concerts.map(concert => (
          <li key={concert.id}>
            {concert.featured_image && (
              <img
                src={concert.featured_image.source_url}
                alt={concert.featured_image.alt_text}
                style={{ maxWidth: '100%', height: 'auto' }}
              />
            )}
            <h3>{concert.title}</h3>
            <p>Date et Heure: {concert.start_date}</p>
            <p>Lieu: {concert.venue.venue}</p>
            {/* Ajoutez d'autres détails spécifiques à votre programmation */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Programmation;
