import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Partners.css';

const PartnersComponent = () => {
  const [partners, setPartners] = useState([]);

  useEffect(() => {
    // Remplacez l'URL ci-dessous par l'URL de votre API WordPress
    const apiUrl = 'http://cjezdhm.cluster029.hosting.ovh.net/wp-json/wp/v2/partners?per_page=20';

    axios.get(apiUrl)
      .then(response => {
        console.log('Partners API response:', response.data);
        setPartners(response.data);
      })
      .catch(error => {
        console.error('Error fetching partners:', error);
      });
  }, []);

  const extractImageFromContent = content => {
    const parser = new DOMParser();
    const doc = parser.parseFromString(content, 'text/html');
    const imgElement = doc.querySelector('img');

    return imgElement ? imgElement.getAttribute('src') : null;
  };

  // Filtrer les partenaires par catégorie
  const mediaPartners = partners.filter(partner => (
    partner.categories.some(category => category === 16) // ID de la catégorie "Partenaires médias"
  ));

  const institutionalPartners = partners.filter(partner => (
    partner.categories.some(category => category === 15) // ID de la catégorie "Partenaires institutionnels"
  ));

  return (
    <div className='partenaires text-center pt-5 pb-5 px-3'>
      <h2>PARTENAIRES</h2>
      <div>
        <h3>Partenaires Médias</h3>
        {mediaPartners.map(partner => (
          <div key={partner.id}>
            <h4>{partner.title.rendered}</h4>
            {partner.content && (
              <img src={extractImageFromContent(partner.content.rendered)} alt={partner.title.rendered} />
            )}
          </div>
        ))}
      </div>
      <div>
        <h3>Partenaires Institutionnels</h3>
        {institutionalPartners.map(partner => (
          <div key={partner.id}>
            <h4>{partner.title.rendered}</h4>
            {partner.content && (
              <img src={extractImageFromContent(partner.content.rendered)} alt={partner.title.rendered} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PartnersComponent;
